import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, useHistory } from 'react-router-dom';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';
import FinalStep from './FinalStep';
import Confirmation from './Confirmation';
import ProgressTracker from './ProgressTracker';

const FormWizard = () => {
  const [formData, setFormData] = useState({});
  const [currentStep, setCurrentStep] = useState(1);
  const history = useHistory();

  const updateFormData = (stepData) => {
    setFormData(prevData => ({ ...prevData, ...stepData }));
  };

  const nextStep = () => {
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
      history.push(`/step${currentStep + 1}`);
    } else {
      history.push('/confirmation');
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
      history.push(`/step${currentStep - 1}`);
    }
  };

  const renderStep = (Step) => {
    return (
      <Step
        formData={formData}
        updateFormData={updateFormData}
        nextStep={nextStep}
        prevStep={prevStep}
      />
    );
  };

  return (
    <Router>
      <div>
        <ProgressTracker currentStep={currentStep} />
        <Switch>
          <Route path="/step1">{renderStep(Step1)}</Route>
          <Route path="/step2">{renderStep(Step2)}</Route>
          <Route path="/step3">{renderStep(Step3)}</Route>
          <Route path="/step4">{renderStep(FinalStep)}</Route>
          <Route path="/confirmation">
            <Confirmation formData={formData} />
          </Route>
          <Redirect from="/" to="/step1" />
        </Switch>
      </div>
    </Router>
  );
};

export default FormWizard;
