import React, { useState } from 'react';
import Step1 from './Step1';
import Step2 from './Step2';
import FinalStep from './FinalStep';

const FormWizard = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  return (
    <div>
      <h1 className="text-3xl font-bold mb-4">Audiology Plus Onboarding</h1>
      <p className="mb-6">Complete the following steps to set up your account.</p>
      {step === 1 && <Step1 nextStep={nextStep} handleInputChange={handleInputChange} formData={formData} />}
      {step === 2 && <Step2 nextStep={nextStep} prevStep={prevStep} handleInputChange={handleInputChange} formData={formData} />}
      {step === 3 && <FinalStep formData={formData} prevStep={prevStep} />}
    </div>
  );
};

export default FormWizard;
